import { useState } from "react";

let products = [{
    id: 101,
    imgUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQhU7dJtF9rLXtb1_e30rF-ch_i4Ex63adbA&s",
    name: "Samsung",
    price: 60000,
    qty: 1
},
{
    id: 102,
    imgUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQhU7dJtF9rLXtb1_e30rF-ch_i4Ex63adbA&s",
    name: "I Phone",
    price: 80000,
    qty: 1
},
{
    id: 103,
    imgUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQhU7dJtF9rLXtb1_e30rF-ch_i4Ex63adbA&s",
    name: "Google Pixel",
    price: 40000,
    qty: 1
}];

export default function Cart() {

    let [items, setItem] = useState(products);

    function qtyInc(id){
       let updateArr= items.map((item)=>{
            if(item.id==id){
                return {...item,qty:item.qty+1};
            }else return item;
        });

        setItem(updateArr);
    }

    function qtyInc(id){
       let updateArr= items.map((item)=>{
            if(item.id==id){
                return {...item,qty:(item.qty>1)? item.qty+1:1};
            }else return item;
        });

        setItem(updateArr);
    }


    return (
        <>
            <section className="container-fluid mt-3">
                <div className="row">
                    <div className="col">
                        <h1> 🛒 Cart:</h1>
                        <p>
                            Lorem ipsum dolor sit, amet consectetur adipisicing elit. Iure culpa necessitatibus expedita voluptate. Recusandae, ipsam! Sit labore maxime, ab iure neque accusamus, eligendi suscipit officia voluptate aut tenetur quibusdam ipsum
                        </p>
                    </div>
                </div>
            </section>

            <section className="container-fluid">
                <div className="btn btn-dark">Buy Now</div>
                <table className="table text-center table-bordered mt-2">
                    <thead>
                        <tr className="table-dark">
                            <th>Id</th>
                            <th>Image</th>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Total</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((item)=>{
                             let { id, imgUrl, name, price, qty } = item;
                            return <tr>
                            <td>{id}</td>
                            <td><img src={imgUrl} alt="" width="30" height="30" /></td>
                            <td>{name}</td>
                            <td>{price}</td>
                            <td>
                                <i className="btn fa fa-minus-circle"></i>
                                {qty}
                                <i className="btn fa fa-plus-circle" onClick={()=>qtyInc(id)}></i></td>
                            <td>{price * qty}</td>
                        </tr>
                        })}
                    </tbody>
                </table>
            </section>
        </>
    );
}
