import Navbar from "./Navbar";
let ThemeContext = createContext();

export default function ContextAPI(){
    let [theme,setTheme]=useState("light");
    return (
        <>
         <Navbar/>
         <ThemeContext.Provider value={theme}>
            <Catagories/>
         </ThemeContext.Provider>
        </>
    );
}

function Catagories(){
    return (
        <>
            <h1>Catagories</h1>
            <Products/>
        </>
    );
}

