import React, { use, useState } from "react";

export default function TodoList() {
    let [items,setItems]=useState([]);

    function handleAddItem(item){
        
        setItems((items)=>{
            return [...items,item];
        });
    }

    return (
        <>
            <Form addList={handleAddItem}/>
            <AllItems itemlist/>
        </>
    );

}

function Form({addList}) {
    
    let [itemName,setItem] = useState("");
    let [qty,setQty] = useState(0);

    function addItem(e){
        e.preventDefault();
        let newObj = {id:Math.round(Math.random()*5),itemName,qty,carried:false}
        addList(itemName);
        itemName="";
        qty=0;
    }

    return (
        <>
            <section className="container-fluid mt-5">
                <div className="row">
                    <div className="col-5 m-auto">
                        <div className="card">
                            <div className="card-header bg-dark text-white text-center">
                                <h1>Todo List</h1>
                            </div>
                            <div className="card-body">
                                <form onSubmit={addItem}>
                                    <div className="mb-2">
                                        <input type="text" onChange={(e)=>setItem(e.target.value)} className="form-control" placeholder="Enter Item Name" />
                                    </div>
                                    <div className="mb-2">
                                        <input type="text" onChange={(e)=>setQty(e.target.value)} className="form-control" placeholder="Enter Quantity" />
                                    </div>
                                    <input type="submit" name="" id="" className="btn btn-dark"/>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}

function AllItems(){
    return (
        <>
        <div className="container-fluid">
            <div className="row mt-3">
                <div className="col-5 m-auto">
                    <div className="card">
                        <ListItems/>
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

function ListItems(){
    return(
        <>
        <ul className="list-group gap-2">
            <li className="list-group-item fw-bold bg-dark-subtle d-flex align-items-center justify-content-between">
                laptop
                <div className="btn">❌</div>
            </li>
        </ul>
        </>
    );
}
