import React, { useState,useEffect } from "react";

export default function TodoList() {
    let [items,setItems]=useState([]);

    function handleAddItem(item){
        
        setItems((items)=>{
            return [...items,item];
        });
    }

    function handleDelete(id){
        setItems((items)=>{
            return items.filter((item)=>item.id !== id);
        });
    }

    return (
        <>
            <Form addList={handleAddItem}/>
            <AllItems itemlist={items} onDelete={handleDelete}/>
        </>
    );

}

function Form({addList}) {
    
    let [itemName,setItemName] = useState("");
    let [qty,setQty] = useState(0);

    function addItem(e){
        e.preventDefault();
        let newObj = {id:Math.round(Math.random()*5),itemName,qty,carried:false}
        addList(newObj);
        setItemName("");
        setQty("")
        console.log(itemName, qty);
        
    }

    useEffect(()=>{
setItemName("");
        setQty("")
    },[itemName])

    return (
        <>
            <section className="container-fluid mt-5">
                <div className="row">
                    <div className="col-5 m-auto">
                        <div className="card">
                            <div className="card-header bg-dark text-white text-center">
                                <h1>Todo List</h1>
                            </div>
                            <div className="card-body">
                                <form onSubmit={addItem}>
                                    <div className="mb-2">
                                        <input type="text" onChange={(e)=>setItemName(e.target.value)} className="form-control" placeholder="Enter Item Name" />
                                    </div>
                                    <div className="mb-2">
                                        <input type="text" onChange={(e)=>setQty(e.target.value)} className="form-control" placeholder="Enter Quantity" />
                                    </div>
                                    <input type="submit" name="" id="" className="btn btn-dark"/>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}

function AllItems({itemlist,onDelete}){
    return (
        <>
        <div className="container-fluid">
            <div className="row mt-3">
                <div className="col-5 m-auto">
                    <div className="card">
                        {
                            itemlist.map((item)=>(
                                <ListItems item={item} key={item.id} onDelete={onDelete} />
                            ))
                        }
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

function ListItems({item,onDelete}){
    return(
        <>
        <ul className="list-group gap-2">
            <li className="list-group-item fw-bold bg-dark-subtle d-flex align-items-center justify-content-between mt-2">
                {item.qty} {item.itemName}
                <div className="btn" onClick={()=>onDelete(item.id)}>❌</div>
            </li>
        </ul>
        </>
    );
}
